describe WebpagesController do

  # This should return the minimal set of attributes required to create a valid
  # Webpage. As you add validations to Webpage, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    { "title" => "MyString" }
  end

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # WebpagesController. Be sure to keep this updated too.
  def valid_session
    {}
  end

  describe "GET index" do
    it "assigns all webpages as @webpages" do
      webpage = Webpage.create! valid_attributes
      get :index, {}, valid_session
      assigns(:webpages).should eq([webpage])
    end
  end

  describe "GET show" do
    it "assigns the requested webpage as @webpage" do
      webpage = Webpage.create! valid_attributes
      get :show, {:id => webpage.to_param}, valid_session
      assigns(:webpage).should eq(webpage)
    end
  end

  describe "GET new" do
    it "assigns a new webpage as @webpage" do
      get :new, {}, valid_session
      assigns(:webpage).should be_a_new(Webpage)
    end
  end

  describe "GET edit" do
    it "assigns the requested webpage as @webpage" do
      webpage = Webpage.create! valid_attributes
      get :edit, {:id => webpage.to_param}, valid_session
      assigns(:webpage).should eq(webpage)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Webpage" do
        expect {
          post :create, {:webpage => valid_attributes}, valid_session
        }.to change(Webpage, :count).by(1)
      end

      it "assigns a newly created webpage as @webpage" do
        post :create, {:webpage => valid_attributes}, valid_session
        assigns(:webpage).should be_a(Webpage)
        assigns(:webpage).should be_persisted
      end

      it "redirects to the created webpage" do
        post :create, {:webpage => valid_attributes}, valid_session
        response.should redirect_to(Webpage.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved webpage as @webpage" do
        # Trigger the behavior that occurs when invalid params are submitted
        Webpage.any_instance.stub(:save).and_return(false)
        post :create, {:webpage => { "title" => "invalid value" }}, valid_session
        assigns(:webpage).should be_a_new(Webpage)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Webpage.any_instance.stub(:save).and_return(false)
        post :create, {:webpage => { "title" => "invalid value" }}, valid_session
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested webpage" do
        webpage = Webpage.create! valid_attributes
        # Assuming there are no other webpages in the database, this
        # specifies that the Webpage created on the previous line
        # receives the :update_attributes message with whatever params are
