        Webpage.any_instance.should_receive(:update_attributes).with({ "title" => "MyString" })
        put :update, {:id => webpage.to_param, :webpage => { "title" => "MyString" }}, valid_session
      end

      it "assigns the requested webpage as @webpage" do
        webpage = Webpage.create! valid_attributes
        put :update, {:id => webpage.to_param, :webpage => valid_attributes}, valid_session
        assigns(:webpage).should eq(webpage)
      end

      it "redirects to the webpage" do
        webpage = Webpage.create! valid_attributes
        put :update, {:id => webpage.to_param, :webpage => valid_attributes}, valid_session
        response.should redirect_to(webpage)
      end
    end

    describe "with invalid params" do
      it "assigns the webpage as @webpage" do
        webpage = Webpage.create! valid_attributes
        # Trigger the behavior that occurs when invalid params are submitted
        Webpage.any_instance.stub(:save).and_return(false)
